function BasicMenu() {
  const { activeAccount, providers, connectedAccounts } = useWallet();
  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);
  const open = Boolean(anchorEl);
  const handleClick = (event: React.MouseEvent<HTMLButtonElement>) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };
  /* Theme */
  const isDarkTheme = useSelector(
    (state: RootState) => state.theme.isDarkTheme
  );
  return (
    <div>
      {!activeAccount ? (
        <AccountDropdown
          className={isDarkTheme ? "dark" : "light"}
          onClick={(e: any) => {
            e.preventDefault();
            handleClick(e);
          }}
        >
          <AccountDropdownLabel>Connect</AccountDropdownLabel>
          <WalletIconContainer>
            <WalletIcon2 />
          </WalletIconContainer>
        </AccountDropdown>
      ) : (
        <AccountDropdown
          id="basic-button"
          aria-controls={open ? "basic-menu" : undefined}
          aria-haspopup="true"
          aria-expanded={open ? "true" : undefined}
          onClick={(e: any) => {
            handleClick(e);
          }}
        >
          <AccountDropdownLabel className="light">
            {activeAccount?.address.slice(0, 4)}
          </AccountDropdownLabel>
          {/*<img
            style={{
              transform: open ? "rotate(180deg)" : "rotate(0deg)",
              transition: "transform 0.3s",
            }}
            src={ArrowDownwardIcon}
          />*/}
          <StyledWalletIcon />
        </AccountDropdown>
      )}

      <AccountMenu
        id="basic-menu"
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
        MenuListProps={{
          "aria-labelledby": "basic-button",
        }}
        PaperProps={{
          elevation: 0,
          sx: {
            display: "inline-flex",
            padding: "18px",
            flexDirection: "column",
            alignItems: "flex-start",
            gap: "24px",
            borderRadius: "16px",
            overflow: "visible",
            filter: "drop-shadow(0px 2px 8px rgba(0,0,0,0.32))",
            mt: 2,
            "& .MuiAvatar-root": {
              width: 32,
              height: 32,
              ml: -0.5,
              mr: 1,
            },
            /*
            "&::before": {
              content: '""',
              display: "block",
              position: "absolute",
              top: 0,
              right: 20,
              width: 20,
              height: 20,
              bgcolor: "background.paper",
              transform: "translateY(-50%) rotate(45deg)",
              zIndex: 0,
            },
            */
          },
        }}
        transformOrigin={{ horizontal: "right", vertical: "top" }}
        anchorOrigin={{ horizontal: "right", vertical: "bottom" }}
      >
        <WalletContainer>
          {providers?.map((provider) => {
            return (
              <ProviderContainer>
                <ProviderIconContainer>
                  <ProviderName>
                    <WalletIcon
                      style={{
                        background: `url(${provider.metadata.icon}) lightgray 50% / cover no-repeat`,
                      }}
                    />
                    <ProviderNameLabel>
                      {provider.metadata.name}
                    </ProviderNameLabel>
                    <Wallet />
                  </ProviderName>
                  {connectedAccounts?.some(
                    (el: any) => el.providerId === provider.metadata.id
                  ) ? (
                    <Box
                      onClick={(e: any) => {
                        const provider = providers?.find(
                          (p) => p.metadata.id === activeAccount?.providerId
                        );
                        provider?.disconnect();
                      }}
                    >
                      <DisconnectButton />
                    </Box>
                  ) : (
                    <Box
                      onClick={(e: any) => {
                        provider?.connect();
                        if (provider?.metadata.id !== PROVIDER_ID.KIBISIS) {
                          setAnchorEl(null);
                        }
                      }}
                    >
                      <ConnectButton />
                    </Box>
                  )}
                </ProviderIconContainer>
                <ConnectedAccountContainer>
                  {connectedAccounts
                    ?.filter((a) => a.providerId === provider.metadata.id)
                    .map((account) => {
                      return (
                        <AccountContainer>
                          <AccountNameContainer>
                            <AccountName>
                              {account.address.slice(0, 4)}
                            </AccountName>
                          </AccountNameContainer>
                          <ActiveButtonContainer>
                            {account.address !== activeAccount?.address ? (
                              <ActiveButton
                                onClick={(e: any) => {
                                  provider?.setActiveProvider();
                                  provider?.setActiveAccount(account.address);
                                }}
                              >
                                Set Active
                              </ActiveButton>
                            ) : null
                            /*
                              <Link to={`/account/${account.address}`}>
                                <ActiveButton>View gallery</ActiveButton>
                              </Link>
                              */
                            }
                          </ActiveButtonContainer>
                        </AccountContainer>
                      );
                    })}
                </ConnectedAccountContainer>
              </ProviderContainer>
            );
          })}
        </WalletContainer>
        {/*<MenuItem
          onClick={(e) => {
            const provider = providers?.find(
              (p) => p.metadata.id === activeAccount?.providerId
            );
            provider?.disconnect();
          }}
        >
          Disconnect
        </MenuItem>
        */}
      </AccountMenu>
    </div>
  );
}

export default BasicMenu;
